package A73;

public class ddddd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum = 0;
		for(int a = 0; a <= 10; a++ ,sum = sum+a) {
			System.out.print(a);
			if(a==10) {System.out.print("=");
			System.out.print(sum);
			}
			else {System.out.print("+");}
	}

	}}
